CREATE DATABASE IF NOT EXISTS dogodkivmojiblizini;
USE dogodkivmojiblizini;

SET FOREIGN_KEY_CHECKS = 0;

DROP TABLE IF EXISTS cenik;
DROP TABLE IF EXISTS dogodek;
DROP TABLE IF EXISTS komentar;
DROP TABLE IF EXISTS najljubsi_dogodki;
DROP TABLE IF EXISTS najljubsi_organizator;
DROP TABLE IF EXISTS naslov;
DROP TABLE IF EXISTS prijava;
DROP TABLE IF EXISTS tip_dogodka;
DROP TABLE IF EXISTS uporabnik;
DROP TABLE IF EXISTS ocena;

SET FOREIGN_KEY_CHECKS = 1;

CREATE TABLE uporabnik (
  idUporabnik INT PRIMARY KEY AUTO_INCREMENT,
  ime VARCHAR(45) NOT NULL,
  priimek VARCHAR(45) NOT NULL,
  email VARCHAR(100) NOT NULL UNIQUE,
  geslo VARCHAR(500) NOT NULL,
  datum_rojstva DATE NOT NULL,
  tip_uporabnika VARCHAR(50) NOT NULL,
  slika VARCHAR(200),
  slika_id VARCHAR(200) NULL,
  is_banned BOOLEAN NOT NULL DEFAULT FALSE
);

CREATE TABLE naslov (
  idNaslov INT PRIMARY KEY AUTO_INCREMENT,
  ulica VARCHAR(45) NOT NULL,
  hisna_stevilka VARCHAR(10) NOT NULL,
  postna_stevilka VARCHAR(10) NOT NULL,
  obcina VARCHAR(45) NOT NULL,
  lat DECIMAL(10, 8) NULL,
  lon DECIMAL(11, 8) NULL
);

CREATE TABLE tip_dogodka (
  idTip_dogodka INT PRIMARY KEY AUTO_INCREMENT,
  naziv VARCHAR(45) NOT NULL
);

CREATE TABLE dogodek (
  idDogodek INT PRIMARY KEY AUTO_INCREMENT,
  naziv VARCHAR(100) NOT NULL,
  cas DATETIME NOT NULL,
  opis LONGTEXT NOT NULL,
  slika VARCHAR(200) NULL,
  slika_id VARCHAR(200) NULL,
  je_promoviran TINYINT NULL DEFAULT 0,
  eventim_url VARCHAR(200) NULL,
  TK_naslov INT NOT NULL,
  TK_tip_dogodka INT NOT NULL,
  TK_uporabnik INT NOT NULL,
  CONSTRAINT FK_dogodek_naslov FOREIGN KEY (TK_naslov) REFERENCES naslov (idNaslov)
    ON DELETE RESTRICT
    ON UPDATE CASCADE,
  CONSTRAINT FK_dogodek_tip FOREIGN KEY (TK_tip_dogodka) REFERENCES tip_dogodka (idTip_dogodka)
    ON DELETE RESTRICT
    ON UPDATE CASCADE,
  CONSTRAINT FK_dogodek_organizator FOREIGN KEY (TK_uporabnik) REFERENCES uporabnik (idUporabnik)
    ON DELETE RESTRICT
    ON UPDATE CASCADE
);

CREATE TABLE cenik (
  idCenik INT PRIMARY KEY AUTO_INCREMENT,
  datum_od DATE NOT NULL,
  datum_do DATE NULL,
  cena DECIMAL(6,2) NOT NULL,
  TK_dogodek INT NOT NULL,
  CONSTRAINT FK_cenik_dogodek FOREIGN KEY (TK_dogodek) REFERENCES dogodek (idDogodek)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

CREATE TABLE ocena (
	idOcena INT PRIMARY KEY AUTO_INCREMENT,
	ocena INT NULL CHECK (ocena BETWEEN 1 AND 5),
    TK_dogodek INT NOT NULL,
    TK_uporabnik INT NOT NULL,
    CONSTRAINT TK_ocena_dogodek FOREIGN KEY (TK_dogodek) REFERENCES dogodek(idDogodek)
		ON DELETE CASCADE
        ON UPDATE CASCADE,
	CONSTRAINT TK_ocena_uporabnik FOREIGN KEY (TK_uporabnik) REFERENCES uporabnik (idUporabnik)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

CREATE TABLE komentar (
  idKomentar INT PRIMARY KEY AUTO_INCREMENT,
  text_uporabnika LONGTEXT NOT NULL,
  ocena INT NULL CHECK (ocena BETWEEN 1 AND 5),
  datum DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  TK_uporabnik INT NOT NULL,
  TK_dogodek INT NOT NULL,
  CONSTRAINT TK_uporabnik FOREIGN KEY (TK_uporabnik) REFERENCES uporabnik (idUporabnik)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
  CONSTRAINT TK_dogodek FOREIGN KEY (TK_dogodek) REFERENCES dogodek (idDogodek)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

CREATE TABLE najljubsi_dogodki (
  idNajljubsiDogodek INT PRIMARY KEY AUTO_INCREMENT,
  TK_dogodek INT NOT NULL,
  TK_uporabnik INT NOT NULL,
  CONSTRAINT FK_najljubsi_dogodek FOREIGN KEY (TK_dogodek) REFERENCES dogodek (idDogodek)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
  CONSTRAINT FK_najljubsi_uporabnik FOREIGN KEY (TK_uporabnik) REFERENCES uporabnik (idUporabnik)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

CREATE TABLE najljubsi_organizator (
  idNajljubsiOrganizator INT PRIMARY KEY AUTO_INCREMENT,
  TK_uporabnik INT NOT NULL,
  TK_uporabnik_organizator INT NOT NULL,
  CONSTRAINT FK_najljubsi_organizator_uporabnik FOREIGN KEY (TK_uporabnik) REFERENCES uporabnik (idUporabnik)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
  CONSTRAINT FK_najljubsi_organizator_organizator FOREIGN KEY (TK_uporabnik_organizator) REFERENCES uporabnik (idUporabnik)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

CREATE TABLE prijava (
  idPrijava INT PRIMARY KEY AUTO_INCREMENT,
  TK_dogodek INT NOT NULL,
  TK_uporabnik INT NOT NULL,
  CONSTRAINT FK_prijava_dogodek FOREIGN KEY (TK_dogodek) REFERENCES dogodek (idDogodek)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
  CONSTRAINT FK_prijava_uporabnik FOREIGN KEY (TK_uporabnik) REFERENCES uporabnik (idUporabnik)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

